import React, { useState, useEffect } from 'react';
import './FloatingButton.css';

const FloatingButton = () => {
  const [isMobile, setIsMobile] = useState(false);
  const [showButtons, setShowButtons] = useState(false);

  useEffect(() => {
    const handleWindowResize = () => {
      setIsMobile(window.innerWidth <= 800);
    };

    window.addEventListener('resize', handleWindowResize);

    return () => {
      window.removeEventListener('resize', handleWindowResize);
    };
  }, []);

  const handleClick = () => {
    setShowButtons(!showButtons);
  };

  return (
    <>
      <button
        className={`floating-button ${isMobile ? 'mobile' : ''}`}
        onClick={handleClick}
      >
        <span className="floating-button-icon">+</span>
      </button>
      {showButtons && (
        <div className="floating-button-group">
          <button className="floating-button">Button 1</button>
          <button className="floating-button">Button 2</button>
          <button className="floating-button">Button 3</button>
        </div>
      )}
    </>
  );
};

export default FloatingButton;
